import org.junit.Test;
import static org.junit.Assert.*;

public class ArrayChallengeTest {
    @Test
    public void testFindMaxValue() {
        int[] testArray = {5, 10, 3, 8, 15, 2};
        assertEquals(15, ArrayChallenge.findMaxValue(testArray));
    }

@Test(expected = IllegalArgumentException.class)
    public void testEmptyArray() {
        int[] emptyArray = {};
        ArrayChallenge.findMaxValue(emptyArray);
    }

@Test
    public void testSingleElementArray() {
        int[] singleArray = {42};
        assertEquals(42, ArrayChallenge.findMaxValue(singleArray));
    }
