from solution import create_greeting

def test_create_greeting_basic():
    assert create_greeting('Alice', 30) == 'Hi Alice, you are 30 years old!'

def test_create_greeting_different_inputs():
    assert create_greeting('Bob', 25) == 'Hi Bob, you are 25 years old!'

def test_create_greeting_zero_age():
    assert create_greeting('Charlie', 0) == 'Hi Charlie, you are 0 years old!'
