tests:
def test_basic_string():
    assert process_string('Hello World') == 'hello_world'
    assert process_string('Python Programming') == 'python_programming'

def test_whitespace():
    assert process_string('  Hello  World  ') == 'hello_world'
    assert process_string('\tPython\n') == 'python'

def test_empty_string():
    assert process_string('') == ''
    assert process_string('   ') == ''
