from solution import safe_convert

def test_safe_convert_int():
    assert safe_convert('123') == 123
    assert safe_convert('abc', default=0) == 0

def test_safe_convert_float():
    assert safe_convert('123.45', target_type=float) == 123.45
    assert safe_convert('xyz', target_type=float, default=0.0) == 0.0

def test_safe_convert_str():
    assert safe_convert(456, target_type=str) == '456'
