from solution import update_inventory

def test_update_inventory_new_items():
    current = {'apples': 5, 'bananas': 3}
    new = {'oranges': 4, 'apples': 2}
    result = update_inventory(current, new)
    assert result == {'apples': 7, 'bananas': 3, 'oranges': 4}

def test_update_inventory_existing_items():
    current = {'apples': 5, 'bananas': 3}
    new = {'apples': 3, 'bananas': 2}
    result = update_inventory(current, new)
    assert result == {'apples': 8, 'bananas': 5}

def test_update_inventory_empty():
    current = {}
    new = {'oranges': 4}
    result = update_inventory(current, new)
    assert result == {'oranges': 4}
