from solution import create_contact_book

def test_create_contact_book():
    contacts = [('Alice', '123-456'), ('Bob', '789-012')]
    result = create_contact_book(contacts)
    assert result == {'Alice': '123-456', 'Bob': '789-012'}

def test_empty_contact_book():
    contacts = []
    result = create_contact_book(contacts)
    assert result == {}

def test_multiple_contacts():
    contacts = [('Alice', '123-456'), ('Bob', '789-012'), ('Charlie', '345-678')]
    result = create_contact_book(contacts)
    assert len(result) == 3
